package study;

/* 실수형의 기본 자료형은 double 형이다.
 *  float f1 = 3.4f;    f 가 명시되어야 함
 *  float f2 = 34.4;    되어있지 않으면 doubel 로 간주되어 에러
 *  double f2 = 34.4
 */



public class FloatEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        float f1 = 3.4f;
		double f2 = 34.4;
		
		System.out.println("f1 :"+f1);
		System.out.println("f2 :"+f2);
		
	}

}
