package InputOutput;

// &, |, ^


public class BitTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 13, b = 7;
		
		System.out.println("a & b : " + (a & b));
		System.out.println("a | b : " + (a | b));
		System.out.println("a ^ b : " + (a ^ b));
		

	}

}
